mapper.update:
UPDATE `order`
order_number = #{orderNumber}, user_id = #{userId}, train_id = #{trainId}, departure_station_id = #{departureStationId}, arrival_station_id = #{arrivalStationId}, departure_date = #{departureDate}, departure_time = #{departureTime}, arrival_time = #{arrivalTime}, seat_type = #{seatType}, passenger_id_card = #{passengerIdCard}, passenger_real_name = #{passengerRealName}, ticket_price = #{ticketPrice}, order_status = #{orderStatus}, pay_status = #{payStatus}, pay_time = #{payTime}, expire_time = #{expireTime}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT *
FROM `order`
WHERE train_id = #{trainId}
AND DATE(departure_date) = #{departureDate}
ORDER BY create_time DESC
