mapper.insert:
INSERT INTO seat (
    carriage_id, seat_number, seat_type, row_num, col_num, status, create_time
) VALUES (
             #{carriageId}, #{seatNumber}, #{seatType}, #{rowNum}, #{colNum}, #{status}, #{createTime}
         )

mapper.update:
UPDATE seat
carriage_id = #{carriageId}, seat_number = #{seatNumber}, seat_type = #{seatType}, row_num = #{rowNum}, col_num = #{colNum}, status = #{status},
WHERE id = #{id}

mapper.select:
SELECT *
FROM seat
WHERE carriage_id = #{carriageId} AND seat_number = #{seatNumber}

mapper.update:
UPDATE seat
SET status = #{status}
WHERE id = #{id}
