mapper.insert:
INSERT INTO ticket_inventory (
    train_id, departure_date, seat_type, total_count,
    available_count, version, create_time, update_time
) VALUES (
             #{trainId}, #{departureDate}, #{seatType}, #{totalCount},
             #{availableCount}, #{version}, #{createTime}, #{updateTime}
         )

mapper.update:
UPDATE ticket_inventory
train_id = #{trainId}, departure_date = #{departureDate}, seat_type = #{seatType}, total_count = #{totalCount}, available_count = #{availableCount}, version = version + 1, update_time = NOW()
WHERE id = #{id} AND version = #{version}

mapper.select:
SELECT *
FROM ticket_inventory
WHERE train_id = #{trainId}
AND departure_date = #{departureDate}
AND seat_type = #{seatType}

SELECT *
FROM ticket_inventory
WHERE train_id = #{trainId}
AND departure_date = #{departureDate}

SELECT *
FROM ticket_inventory
WHERE train_id = #{trainId}
AND departure_date = #{departureDate}
AND seat_type = #{seatType}
FOR UPDATE
